css:
:root {
  font-size: 16px;
}
#linter-tooltip {
  margin-top: 3px;
}
#linter-tooltip linter-message.error {
  color: #ffffff;
  background-color: #F44336;
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #F44336 #F44336;
}
#linter-tooltip linter-message.error a {
  color: #ffffff;
}
#linter-tooltip linter-message.warning {
  color: #ffffff;
  background-color: #d39e00;
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #d39e00 #d39e00;
}
#linter-tooltip linter-message.warning a {
  color: #ffffff;
}
#linter-tooltip linter-message.info {
  color: #ffffff;
  background-color: #2196F3;
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #2196F3 #2196F3;
}
#linter-tooltip linter-message.info a {
  color: #ffffff;
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: rgba(122, 112, 82, 0.6);
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
atom-text-editor.editor .linter-highlight.linter-info .region,
.linter-highlight.linter-info .region {
  background-image: linear-gradient(45deg, transparent 65%, #9acffa 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #9acffa 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #9acffa 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #9acffa 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-info.linter-gutter,
.linter-highlight.linter-info.linter-gutter {
  color: #9acffa;
  z-index: -1;
}
atom-text-editor.editor .linter-highlight.linter-warning .region,
.linter-highlight.linter-warning .region {
  background-image: linear-gradient(45deg, transparent 65%, #fff06e 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fff06e 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fff06e 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fff06e 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning.linter-gutter,
.linter-highlight.linter-warning.linter-gutter {
  color: #fff06e;
  z-index: 0;
}
atom-text-editor.editor .linter-highlight.linter-error .region,
.linter-highlight.linter-error .region {
  background-image: linear-gradient(45deg, transparent 65%, #fbb4af 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fbb4af 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fbb4af 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fbb4af 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error.linter-gutter,
.linter-highlight.linter-error.linter-gutter {
  color: #fbb4af;
  z-index: 1;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #9acffa 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #9acffa 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #9acffa 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #9acffa 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #fff06e 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fff06e 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fff06e 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fff06e 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #fbb4af 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fbb4af 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fbb4af 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fbb4af 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  font-size: 1em;
}
.linter-status-count span {
  border-radius: 0px;
  padding: 4px 6px;
}
.linter-status-count span:first-child {
  border-top-left-radius: 0.125rem;
  border-bottom-left-radius: 0.125rem;
}
.linter-status-count span:last-child {
  border-top-right-radius: 0.125rem;
  border-bottom-right-radius: 0.125rem;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
linter-decoration {
  pointer-events: none;
}
