css:
:root {
  font-size: 16px;
}
.github-PrPaneItem {
  padding: 1.5rem;
  overflow: auto;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  font-size: 1.25em;
  line-height: 1.5;
  background-color: transparent;
}
.github-PrPaneItem * {
  user-select: all;
  -webkit-user-select: all;
}
.github-PrPaneItem-container {
  max-width: 48em;
  margin: 0 auto;
}
.github-PrPaneItem .issueish-badge-and-link {
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
}
.github-PrPaneItem .issueish-badge-and-link :last-child {
  flex: 1;
  text-align: right;
}
.github-PrPaneItem .issueish-link a {
  color: rgba(122, 112, 82, 0.5);
}
.github-PrPaneItem .pr-build-status-icon {
  margin-left: 0.5rem;
}
.github-PrPaneItem .refresh-button .icon {
  color: rgba(122, 112, 82, 0.5);
  cursor: pointer;
}
.github-PrPaneItem .refresh-button .icon.refreshing::before {
  animation: github-RefreshButton-animation 2s linear 30;
}
@keyframes github-RefreshButton-animation {
  100% {
    transform: rotate(360deg);
  }
}
.github-PrPaneItem .issueish-avatar-and-title {
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
.github-PrPaneItem .author-avatar-link {
  margin-right: 0.5rem;
  align-self: flex-start;
}
.github-PrPaneItem .author-avatar {
  max-height: 32px;
  border-radius: 0.125rem;
}
.github-PrPaneItem .issueish-title {
  margin: 0;
  font-size: 1.5em;
  font-weight: 500;
  line-height: 1.3;
  color: rgba(122, 112, 82, 0.6);
}
.github-PrPaneItem .issueish-body {
  padding-top: 0.5rem;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
}
.github-PrPaneItem .issueish-body > *:first-child {
  margin-top: 0;
}
.github-PrPaneItem .github-DotComMarkdownHtml a {
  color: #2196F3;
}
.github-PrPaneItem .github-DotComMarkdownHtml p {
  line-height: 1.5;
}
.github-PrPaneItem .github-DotComMarkdownHtml hr {
  margin-top: 5px;
  margin-bottom: 5px;
  border-top: 1px solid rgba(122, 112, 82, 0.8);
  width: 100%;
}
.github-PrPaneItem .github-DotComMarkdownHtml ul {
  margin-left: -10px;
}
.github-PrPaneItem .github-DotComMarkdownHtml ol {
  margin-left: -20px;
}
.github-PrPaneItem .github-DotComMarkdownHtml li {
  font-size: .9em;
  padding-top: .1em;
  padding-bottom: .1em;
}
.github-PrPaneItem .github-DotComMarkdownHtml li li {
  font-size: 1em;
}
.github-PrPaneItem .github-DotComMarkdownHtml pre > code {
  white-space: pre;
}
.github-PrPaneItem .reactions {
  width: fit-content;
  padding-top: 0.5rem;
}
.github-PrPaneItem .reactions .reaction-group {
  margin-right: 0.5rem;
  padding: 0.5rem;
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 0.125rem;
}
.github-PrPaneItem .reactions:empty {
  all: unset;
}
.github-PrPaneItem .github-PrStatuses {
  margin-top: 2rem;
  padding: 0.5rem;
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 0.125rem;
}
.github-PrPaneItem blockquote {
  font-size: inherit;
  padding-top: 0;
  padding-left: 0.5rem;
  padding-bottom: 0;
  color: rgba(122, 112, 82, 0.5);
  border-left: 2px solid rgba(122, 112, 82, 0.5);
}
